package com.employee.EmployeeCurdOperations.controlleradvice;import com.employee.EmployeeCurdOperations.customexceptions.UserNotFoundExecption;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;@ControllerAdvicepublic class EmployeeControllerAdvice {    @ExceptionHandler(value = UserNotFoundExecption.class)    public ResponseEntity userNotFoundHandler(){        ResponseEntity response = new ResponseEntity("User Not Found",                HttpStatus.valueOf(404));        return response;    }}